import './App.css';
import { useEffect, useState } from 'react';
import axios from 'axios';

function App() {

  const [allData, setAllData] = useState({
    city:'',
    country:'',
    temperature:''
  })

  useEffect(() => {
    // actions after rendering
    // fetch the database info - API to request weather from WEATHER DATABASE
    fetchData()
  },[])

  const fetchData = async (city) => {
    // try catch - error handling
    try{
      const APIKEY = 'fc508fd00391873c1774a7c00017bb7e'
      // axios - library to make request to the backend with promise
      const result = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${'city'}&appid=${APIKEY}`)
      await setAllData({
        city: result,
        country: result.data.sys.country,
        temperature: result
      })
    } catch (e) {
      console.log('API - Down')
    }
  }

  return ( 
    <div className="App">
      {console.log('>>TEST 001>>', allData.country)}
    </div>
  );
}

export default App;
